dunes-script-module.script:
// Retrieve User from Cloud
var aviNsxtCloud = avivroClient.getObjectDataByName("cloud",nsxtCloud);
aviNsxtCloud = JSON.parse(aviNsxtCloud)

// Rretrieve Cred UUID. Trim output to only UUID
var credUUID = (aviNsxtCloud['nsxt_configuration']['nsxt_credentials_ref']).split('api/cloudconnectoruser/')[1]
credUUID = credUUID.split('#')[0]

//Declare NSXT Credentials Object
var aviNsxtCred = new AviNsxtCredentials();

//Retrieve current NSXT Credentials and populate Object
aviNsxtCred = avivroClient.getObjectByUUID("cloudconnectoruser",credUUID);

//Set password for NSXT Credentials Object to Input
aviNsxtCred.NsxtCredentials.setPassword(nsxt_password);

//Add Object to COntroller Queue
avivroClient.addObject(aviNsxtCred);

//Execute Controller Queue. Internal Error Handling.
avivroClient.executeWorkflow();
System.log("Cloud "+nsxtCloud+" updated successfully")
