                "metadata":metadata
            }
    },
        "name":authProfileName
    }

}else if (authType == "ldap"){

    var config2 = {"ldap":{
        "port":ldapPort,
        "bind_as_administrator":true,
        "settings":{
            "user_search_scope":user_search_scope,
            "group_member_attribute":group_member_attribute,
